package main

import (
   "fmt"
   "strings"
   "net/http"
   "io/ioutil"
)

func main() {

   url := "https://test-api.truckerpath.com/truckload/api/webhooks/add"
   method := "POST"

   payload := strings.NewReader(`{
    "url":"https://go.takeleft.co/truckerpath?",
    "type":"BOOK"
}`)

   client := &http.Client {
   }
   req, err := http.NewRequest(method, url, payload)

   if err != nil {
      fmt.Println(err)
      return
   }
   req.Header.Add("Authorization", "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..yj1-NT3kMie3Mg1ZTno2dhvUBEgiZJzXpyLl0i_rACE")
   req.Header.Add("cache-control", "<cache-control>")


   res, err := client.Do(req)
   if err != nil {
      fmt.Println(err)
      return
   }
   defer res.Body.Close()

   body, err := ioutil.ReadAll(res.Body)
   if err != nil {
      fmt.Println(err)
      return
   }
   fmt.Println(string(body))
}

